        double result;
        char operator;
        
    private void button_equalsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_equalsActionPerformed
        num2 = Double.parseDouble(txtField.getText());
        switch (operator){
            case '+':
                result = num1 + num2;
                break;
            case '-':
                result = num1 - num2;
                break;
            case '*':
                result = num1 * num2;
                break; 
            case '/':
                result = num1 / num2;
                break;
                
    }       
            txtField.setText(String.valueOf(result));
    }//GEN-LAST:event_button_equalsActionPerformed
      private void myOpe (char op){
        num1 = Double.parseDouble(txtField.getText());
        txtField.setText("");
        operator = op;
        
        
     }
